INSERT INTO public.websites VALUES (1, 'https://www.iranintl.com/en', 'Iran International', 'Iran International English - Independent news and analysis about Iran', 'Iran-Specific', true, true, NULL, '2025-06-21 16:56:31.522008+00', '2025-06-21 16:56:31.522008+00', 'Volant Media UK Ltd.', 'Iran International''s stated mission is to serve as "a window into Iran from the world, and a window from Iran to the world outside."', 'Its key features are defined by its comprehensive news coverage and multi-platform approach, while its noteworthy aspects are shaped by its oppositional stance, the controversies it attracts, and the severe threats it faces.', '5', 3, 'The channel''s editorial line is undeniably and consistently critical of the Islamic Republic of Iran');
INSERT INTO public.websites VALUES (2, 'https://www.reuters.com/world/middle-east/', 'Reuters', 'Reuters news coverage and analysis about Middle East', 'International', true, true, NULL, '2025-06-21 16:56:31.522008+00', '2025-06-21 16:56:31.522008+00', 'Thomson Reuters, a multinational media conglomerate.', 'The "Trust Principles" mandate independence, integrity, and freedom from bias in the gathering and dissemination of news.', 'Known for its fact-based, agency-style reporting that is widely syndicated. Tends to avoid overt editorializing in its main news output.', '0', 1, 'Gold standard for neutral reporting.');
INSERT INTO public.articles VALUES ('1ea31feb-d09c-45b4-8d50-965ade18b353', 'Iran uses embassies abroad for surveillance and subterfuge, ex-staff say', 'https://www.iranintl.com/en/iran-influence-network', 'Iran uses its overseas missions to covertly surveil dissidents and fund influence operations via state-backed cultural initiatives, multiple former Iranian diplomats and embassy staff members told Iran International.', '2023-10-20 00:00:00+00', 'Iran-Specific', 'Iran International', 'https://www.iranintl.com/en/iran-influence-network', '2025-06-21 17:00:04.33947+00', '2025-06-21 17:16:16.916122+00', NULL, true, '2025-06-21 17:16:16.913+00', 'Iran uses its overseas missions to covertly surveil dissidents and fund influence operations via state-backed cultural initiatives, multiple former Iranian diplomats and embassy staff members told Iran International.', 'Not specified', NULL, NULL, 75, 1, false, NULL, 'en');
INSERT INTO public.articles VALUES ('e443d556-e4ac-43a2-8512-a5d67a6b4619', 'Trump to Decide on Military Strike Against Iran Within Two Weeks', 'https://www.iranintl.com/en/202506198152', 'The White House announced that President Trump will decide within two weeks on a potential military strike against Iran, citing the country proximity to obtaining a nuclear weapon.', '2025-06-19 17:36:07.89+00', 'Iran-Specific', 'Iran International', 'https://www.iranintl.com/en/202506198152', '2025-06-21 17:00:16.41098+00', '2025-06-21 17:16:18.34703+00', NULL, true, '2025-06-21 17:16:18.345+00', 'The White House said on Thursday that President Donald Trump will make a decision within two weeks on whether to launch a military strike on Iran.', 'White House', NULL, NULL, 23, 1, false, NULL, 'en');
INSERT INTO public.articles VALUES ('b5f47d24-9c61-461c-9019-dc335be590f0', 'Iran Issues Military Threat Against Israel Channel 14', 'https://www.iranintl.com/en/202506197226', 'Tehran has issued a military threat and evacuation warning against Israel Channel 14 television, stating that the outlet is expected to be targeted in the coming days.', '2025-06-19 20:39:29.711+00', 'Iran-Specific', 'Iran International', 'https://www.iranintl.com/en/202506197226', '2025-06-21 17:00:22.972963+00', '2025-06-21 17:16:19.302027+00', NULL, true, '2025-06-21 17:16:19.297+00', 'Iranian state-affiliated media reported on Thursday that Tehran has issued a military threat and evacuation warning against Israel Channel 14 television.', 'Iran International', NULL, NULL, 25, 1, false, NULL, 'en');
INSERT INTO public.articles VALUES ('aa127b9d-f7ef-48b0-af7e-d07a35e0ba7a', 'Israeli Air Force Strikes Iranian Military Headquarters', 'https://www.iranintl.com/en/202506193674', 'The Israeli military air force targeted the headquarters of Iran Internal Security Forces in Tehran, part of ongoing attacks on Iran military infrastructure.', '2025-06-19 19:56:52.878+00', 'Iran-Specific', 'Iran International', 'https://www.iranintl.com/en/202506193674', '2025-06-21 17:00:28.777004+00', '2025-06-21 17:16:19.732683+00', NULL, true, '2025-06-21 17:16:19.729+00', 'The Israeli military spokesman said the air force struck the headquarters of the Special Forces unit of Iran Internal Security Forces in Tehran, as part of ongoing attacks on the Islamic Republic military infrastructure.', 'IDF Spokesperson', NULL, NULL, 35, 1, false, NULL, 'en');
INSERT INTO public.articles VALUES ('526f774b-07ea-4e7d-abdb-a07a65cec46a', 'G7 expresses support for Israel, calls Iran a source of instability', 'https://www.reuters.com/world/middle-east/g7-expresses-support-israel-calls-iran-source-instability-2025-06-17', 'The Group of Seven nations expressed support for Israel in a statement and labeled Iran as a source of instability in the Middle East, urging broader de-escalation of hostilities in the region.', '2025-06-17 05:32:38+00', 'International', 'Reuters', 'https://www.reuters.com/world/middle-east/g7-expresses-support-israel-calls-iran-source-instability-2025-06-17', '2025-06-21 17:00:50.553759+00', '2025-06-21 17:16:23.819367+00', NULL, true, '2025-06-21 17:16:23.818+00', 'The Group of Seven nations expressed support for Israel in a statement and labeled Iran as a source of instability in the Middle East, urging broader de-escalation of hostilities in the region.', 'Kanishka Singh', NULL, NULL, 1047, 6, false, NULL, 'en');
